content.input:
{"content":"<script>
  let text = $state("");

  const analysis = $derived.by(() => {
    const trimmedText = text.trim();
    const wordCount = trimmedText === "" ? 0 : trimmedText.split(/\s+/).length;
    const charCount = text.length;
    const isLong = wordCount > 15;

    return {
      wordCount,
      charCount,
      isLong
    };
  });

  function clearText() {
    text = "";
  }
</script>

<div>
  <textarea
    data-testid="text-input"
    bind:value={text}
    placeholder="Enter text to analyze..."
  ></textarea>

  <button data-testid="clear-button" onclick={clearText}>
    Clear
  </button>

  <div data-testid="word-count">Words: {analysis.wordCount}</div>
  <div data-testid="char-count">Characters: {analysis.charCount}</div>
  <div data-testid="length-indicator">
    Status: {analysis.isLong ? "Long text" : "Short text"}
  </div>
</div>
"}

