{"content":"<script>
  let text = $state("Hello world");

  // Basic $inspect to log the value
  $inspect(text);

  // $inspect with custom callback
  $inspect(text).with((type, value) => {
    console.log(`Custom inspect - Type: ${type}, Value: ${value}`);
  });

  // Derived state for character count
  let charCount = $derived(text.length);

  // Effect with $inspect.trace()
  $effect(() => {
    $inspect.trace("Effect triggered");
    // Access text to make effect reactive
    text;
  });
</script>

<div>
  <input 
    type="text" 
    bind:value={text} 
    data-testid="text-input"
  />

  <p data-testid="text-value">Current text: "{text}"</p>
  <p data-testid="char-count">Character count: {charCount}</p>
</div>
"}

